import { Form, Link, useSearchParams, useActionData} from 'react-router-dom';

import classes from './AuthForm.module.css';

function AuthForm() {
  const [serchParams] = useSearchParams()
 const isLogin = serchParams.get('mode') === 'login'
 const data =useActionData()


  return (
    <>
      <Form method="post" className={classes.form}>
        <h1>{isLogin ? 'Log in' : 'Create a new user'}</h1>
        {data && data.errors && (
  <ul>
    {Object.values(data.errors).map((error, index) => (
      <li key={index}>{error.message}</li>
    ))}
  </ul>
)}

          {data && data.message && <p>{data.message}</p>}
        <p>
          <label htmlFor="email">Email</label>
          <input id="email" type="email" name="email" required />
        </p>
        <p>
          <label htmlFor="image">Password</label>
          <input id="password" type="password" name="password" required />
        </p>
        <div className={classes.actions}>
        <Link to={`?mode=${isLogin ? 'signup' : ' '}`}>
          {isLogin ? 'Create new user' : 'Log in'}
        </Link>
          <button>Save</button>
        </div>
      </Form>
    </>
  );
}

export default AuthForm;
